  atlas_occurrences()

butterflies &lt;- galah_call() |&gt;
  filter(doi == "https://doi.org/10.26197/ala.f55459dc-441e-4fd3-9f46-ceeb031aa656") |&gt;
  atlas_occurrences()

bitter_peas &lt;- galah_call() |&gt;
  filter(doi == "https://doi.org/10.26197/ala.706613ae-ee17-471b-90c1-c9fb87830fe4") |&gt;
  atlas_occurrences()

legless_lizards

legless_lizards &lt;- galah_call() |&gt;
  identify("pygopodidae") |&gt;
  filter(year &gt; 2020) |&gt;
  select(group = "basic") |&gt;
  atlas_occurrences()

eucalypts

eucalypts &lt;- galah_call() |&gt;
  identify("Eucalyptus") |&gt;
  filter(eventDate &gt; "2014-01-01T00:00:00Z",
         eventDate &lt; "2014-06-01T00:00:00Z") |&gt;
  select(group = "basic", 
         kingdom, phylum, class, order, 
         family, genus, species, taxonRank) |&gt;
  atlas_occurrences()

frogs

Column classes & names

frogs &lt;- galah_call() |&gt;
  identify("Litoria") |&gt;
  filter(year &gt;= 2020, 
         cl22 == "Tasmania") |&gt;
  select(group = "basic",
         genus, species) |&gt;
  atlas_occurrences()
